import { auth, googleAuthProvider, createUserProfileDocument } from "../../firebase/firebase.utils";
import { call, put, takeLatest, all } from 'redux-saga/effects';
import { signInSuccess, signInFailure } from "./user.actions";

export function* getSnapshotFromUserAuth(userAuth, additionalData) {
    try {
        const userRef = yield call(createUserProfileDocument, userAuth, additionalData);
        const userSnapshot = yield userRef.get();
        yield put(signInSuccess({
            id: userSnapshot.id,
            ...userSnapshot.data()
        }));
    } catch (error) {
        yield put(signInFailure(error));
    }
}  

export function* googleSignInSaga() {
    try {
        const { user } = yield auth.signInWithPopup(googleAuthProvider);
        yield getSnapshotFromUserAuth(user);
    } catch (error) {
        yield put(signInFailure(error));
    }
}

export function* emailSignInSaga({ email, password }) {
    try {
        const { user } = yield auth.signInWithEmailAndPassword(email, password);
        yield getSnapshotFromUserAuth(user); 
    } catch (error) {
        yield put(signInFailure(error));
    }
}

export function* onGoogleSignIn() {
    yield takeLatest('GOOGLE_SIGN_IN_START', googleSignInSaga);
}

export function* onEmailSignIn() {
    yield takeLatest('EMAIL_SIGN_IN_START', emailSignInSaga);
}

export function* userSagas() {
    yield all([
        call(onGoogleSignIn),
        call(onEmailSignIn)
    ]);
}
