// const INITIAL_STATE = {
//     cartDropdownHidden : true
// };

const cartReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case 'TOGGLE_CART_DROPDOWN':
            return {
                ...state,
                cartDropdownHidden: !cartDropdownHidden
            }

        default:
            return state;
    }
};

export default cartReducer;
